import React,{useState} from "react";
import { NavLink } from "react-router-dom";
import {AiOutlineUserAdd} from "react-icons/ai";
import {MdEmail} from "react-icons/md";
import {RiLockPasswordFill} from "react-icons/ri";
function Login() {
//implement useState React hook method,useState is a function which takes to arguments 01)state variable 02)updated function both are in form of array

//useState Hook Syntax:

//const[variable,function] = useState('initial Value')
//This Initial value get store in variable which is first argument of useState
//Second argument is use when the value gets change
   const [userLogin,setUserLogin]=useState({
      email:'',password:''
   })
   const inputHandle=(e)=>{
      let name=e.target.name
      let value=e.target.value
    
      setUserLogin({
        ...userLogin,[name]:value
      })

   }
  return (
   <div className="signup-bg">
    <div className='signup-outer-page'>
      <div className='Login-inner-page'>
        <form>
            <legend>Welcome to TechTej<br></br><AiOutlineUserAdd/></legend>

            
            <fieldset id='login-page'>
                <label for='email'><MdEmail/> Email :</label><br></br>
                <input type='text' name='email' value={userLogin.email} onChange={inputHandle}required/><br></br>
                <label for='password'><RiLockPasswordFill/> Password :</label><br></br>
                <input type='password'name='password' value={userLogin.password}
                onChange={inputHandle} required/><br></br>
                <button id='login-button' >Log in</button>
            </fieldset>
        </form>

                <small id= 'login-small'>Not an user?<NavLink to='/register'>Click here</NavLink></small>
        
      </div>
    </div>
   </div>
  );
}

export default Login;
